mapper.select:
select count(*)
from member
where userid = #{userid}

mapper.update:
update member set
nickName = #{nickName}
where userid = #{userid}

mapper.select:
select count(*)
from member
where addr = #{addr} and username = #{username}

mapper.update:
update member set
addr = #{addr}
where userid = #{userid}

update member
set latest_try_login_date = sysdate
where userid = #{userid}

mapper.select:
SELECT LATEST_TRY_LOGIN_DATE 
from member
where userid = #{userid}

SELECT LOCK_COUNT 
from member
where userid = #{userid}

SELECT login_fail_count 
from member
where userid = #{userid}

mapper.update:
update member
set LOGIN_FAIL_COUNT = 0
where userid = #{userid}

update member
set lock_count = 0 , is_lock = 'N'
where userid = #{userid}

update member
set login_fail_count = login_fail_count + 1 ,latest_try_login_date = sysdate
where userid = #{userid}
